    private void loginButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginButtonActionPerformed
        String user;
        String pwd;
        String type;
        //display a error if the textfield are empty
        if (userNameField.getText().length() == 0) {
            statusAlert.setText("*Please fill up Username");
        } else if (passwordField.getText().length() == 0) {
            statusAlert.setText("*Please fill up Password");
        } else {//If the textfield are not empty
            
            statusAlert.setText("");
            user = userNameField.getText();
            pwd = passwordField.getText();
            type = accountTypeSelection.getSelectedItem().toString();
            if (validateLogin(user, pwd, type)) {
                if (type.equalsIgnoreCase("admin")) {
                    user1.setUserName(user);
                    JOptionPane.showMessageDialog(null, "Login Successful!" + "\n" + "Welcome " + user + "", "Admin", JOptionPane.INFORMATION_MESSAGE);
                    this.dispose();
                    java.awt.EventQueue.invokeLater(() -> {
                        new Home().setVisible(true);
                    });
                } else {
                    JOptionPane.showMessageDialog(null, "Login Successful!" + "\n" + "Welcome " + user + "", "Employee", JOptionPane.INFORMATION_MESSAGE);
                    this.dispose();
                    java.awt.EventQueue.invokeLater(() -> {
                        new Home().setVisible(true);
                    });
                }
            } else {
                JOptionPane.showMessageDialog(null, "Incorrect Login Credentials", "Failed", JOptionPane.WARNING_MESSAGE);
                statusAlert.setText("*Please Try Again");
                passwordField.setText("");
            }
        }
    }//GEN-LAST:event_loginButtonActionPerformed

