    private boolean validateLogin(String user, String pass, String type) {
        final String query = "SELECT userName, password, accountType FROM users WHERE userName = ? and password = ? and accountType = ?";
        try {
            connection = GUI.DB.DBConnection.getConnection();
            ps = connection.prepareStatement(query);
            ps.setString(1, user);
            ps.setString(2, pass);
            ps.setString(3, type);
            rs = ps.executeQuery();

            if (rs.next()) {
                return true;
            } else {
                return false;
            }
        } catch (SQLException e) {
            return false;
        }
    }

    void exit() {
        int showConfirmDialog = JOptionPane.showConfirmDialog(null, "Are you sure?", "Exit", JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE);
        if (showConfirmDialog == JOptionPane.YES_OPTION) {
            System.exit(0);
        }
    }
